import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import Nav from "./Nav";
import { message, Modal } from "antd";
import Loading from "./Loading";

function Profile() {
  const dataInfo = JSON.parse(localStorage.getItem("info"));
  const dataUser = JSON.parse(localStorage.getItem("user"));
  const [data, setData] = useState({
    country: "Vietnam",
    createdAt: "2023-02-26T06:37:49.108Z",
    date: "",
    fullName: "",
    healthInsurance: "SV2821001402141",
    idAccount: dataUser.id,
    location: "",
    mail: "",
    nation: "Kinh",
    passport: "1231232131231",
    phoneNumber: "",
    relative: "Chồng",
    relativeMail: "chunha2411@gmail.com",
    relativeName: "Nguyễn Văn A",
    relativeNumber: "13123123123",
    sex: "Nam",
    state: "Hưng Yên",
    town: "Ha Noi",
    village: "VN",
  });
  const [isLoading, setIsLoading] = useState();
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    (async function () {
      setIsLoading(true);
      try {
        const res = await axios.post(
          "https://alo-server.onrender.com/api/user/data/patient/v2",
          { idAccount: dataUser.id }
        );
        setIsLoading(false);
        localStorage.setItem("info", JSON.stringify(res.data.result));
      } catch (error) {
        setIsLoading(false);
        console.log(error);
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleAddProfole = async () => {
    if (
      data.fullName === "" ||
      data.date === "" ||
      data.location === "" ||
      data.mail === "" ||
      data.phoneNumber === ""
    ) {
      message.error("Vui lòng nhập đầy đủ !");
    } else {
      setIsLoading(true);
      try {
        const res = await axios.post(
          "https://alo-server.onrender.com/api/user/data/patient/v1",
          data
        );
        setIsLoading(false);
        message.success("Add information successfully");
        setTimeout(() => {
          window.location.reload();
        }, 500);
      } catch (error) {
        setIsLoading(false);
        console.log(error);
        message.error("An error occurred  !");
      }
    }
  };
  const handleDelete = async () => {
    setIsLoading(true);
    try {
      const res = await axios.post(
        "https://alo-server.onrender.com/api/user/data/patient/delete/v1",
        {
          _id: dataInfo._id,
        }
      );
      setIsLoading(false);
      localStorage.removeItem("info");
      message.success("Delete information successfully");
    } catch (error) {
      setIsLoading(false);
      console.log(error);
      message.error("An error occurred  !");
    }
  };

  return (
    <>
      {isLoading ? <Loading /> : null}
      <div className="home-container">
        <div className="home-content">
          <div className="home-bg">
            <Nav />
            <div className="profile-container">
              {dataInfo ? (
                <div className="profile-content">
                  <h2>Thông tin</h2>
                  <div>
                    <span>Họ và tên:</span>
                    <p>{dataInfo?.fullName}</p>
                  </div>
                  <div>
                    <span>Ngày sinh:</span>
                    <p>{dataInfo?.date}</p>
                  </div>
                  <div>
                    <span>Email:</span>
                    <p>{dataInfo?.mail}</p>
                  </div>
                  <div>
                    <span>Số điện thoại:</span>
                    <p>{dataInfo?.phoneNumber}</p>
                  </div>
                  <div>
                    <span>Số bảo hiểm:</span>
                    <p>{dataInfo?.healthInsurance}</p>
                  </div>
                  <div>
                    <span>Địa chỉ:</span>
                    <p>{dataInfo?.location}</p>
                  </div>

                  <div className="btn-container">
                    <button className="btn">Sửa hồ sơ</button>
                    <button className="btn" onClick={handleDelete}>
                      Xoá hồ sơ
                    </button>
                  </div>
                </div>
              ) : (
                <div className="profile-content">
                  <h2 style={{ marginBottom: "40px", fontSize: "24px" }}>
                    Bạn chưa có thông tin, vui lòng thêm thông tin !
                  </h2>
                  <div className="form-add" style={{ display: "block" }}>
                    <div>
                      <label htmlFor="">Họ và tên:</label>
                      <input
                        type="text"
                        placeholder="Nhập họ và tên..."
                        onChange={(e) => {
                          setData((pre) => {
                            return { ...pre, fullName: e.target.value.trim() };
                          });
                        }}
                      />
                    </div>
                    <div>
                      <label htmlFor="">Ngày sinh:</label>
                      <input
                        type="text"
                        placeholder="Nhập ngày sinh..."
                        onChange={(e) => {
                          setData((pre) => {
                            return { ...pre, date: e.target.value.trim() };
                          });
                        }}
                      />
                    </div>
                    <div>
                      <label htmlFor="">Email:</label>
                      <input
                        type="text"
                        placeholder="Nhập email..."
                        onChange={(e) => {
                          setData((pre) => {
                            return { ...pre, mail: e.target.value.trim() };
                          });
                        }}
                      />
                    </div>
                    <div>
                      <label htmlFor="">Số điện thoại:</label>
                      <input
                        type="text"
                        placeholder="Nhập số điện thoại..."
                        onChange={(e) => {
                          setData((pre) => {
                            return {
                              ...pre,
                              phoneNumber: e.target.value.trim(),
                            };
                          });
                        }}
                      />
                    </div>

                    <div>
                      <label htmlFor="">Nhập địa chỉ:</label>
                      <input
                        type="text"
                        placeholder="Nhập địa chỉ..."
                        onChange={(e) => {
                          setData((pre) => {
                            return {
                              ...pre,
                              location: e.target.value.trim(),
                            };
                          });
                        }}
                      />
                    </div>
                    <div style={{ textAlign: "center" }}>
                      <button className="btn" onClick={handleAddProfole}>
                        Lưu lại
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Profile;
