import React, { useState } from "react";
import background from "../assets/img/bg.jpeg";
import { NavLink } from "react-router-dom";
import { message } from "antd";
import axios from "axios";
import Loading from "./Loading";
function Login() {
  const [data, setData] = useState({
    userName: "",
    passWord: "",
  });
  const handleLogin = async () => {
    if (data.userName === "" || data.passWord === "") {
      message.error("Vui lòng nhập đủ tài khoản và mật khẩu !");
    } else {
      try {
        const res = await axios.post(
          "https://alo-server.onrender.com/api/user/data/patient",
          data
        );
      } catch (error) {
        message.error(error.response.data.message);
      }
    }
  };
  return (
    <>
      <Loading />
      <div className="login-container">
        <div className="login-content">
          <div className="banner">
            <div className="img">
              <div className="bg-login"></div>
              <div className="form-login">
                <span
                  style={{
                    display: "block",
                    textAlign: "center",
                    fontSize: "3.4rem",
                    margin: "4rem 0 2rem 0",
                  }}
                >
                  Medical
                </span>
                <span
                  style={{
                    display: "block",
                    textAlign: "center",
                    fontSize: "1.6rem",
                  }}
                >
                  Đặt lịch bác sĩ ngay !
                </span>
                <p>Đăng nhập</p>
                <div className="input">
                  <label htmlFor="">Tên tài khoản</label>
                  <input
                    placeholder="Nhập tài khoản..."
                    onChange={(e) => {
                      setData((pre) => {
                        return { ...pre, userName: e.target.value.trim() };
                      });
                    }}
                  />
                </div>

                <div className="input">
                  <label htmlFor="">Mật khẩu</label>
                  <input
                    placeholder="Nhập mật khẩu..."
                    onChange={(e) => {
                      setData((pre) => {
                        return { ...pre, passWord: e.target.value.trim() };
                      });
                    }}
                  />
                </div>

                <div className="input" style={{ textAlign: "center" }}>
                  <button className="btn" onClick={handleLogin}>
                    Đăng nhập
                  </button>
                </div>
                <div
                  className="input"
                  style={{
                    textAlign: "center",
                    display: "flex",
                    justifyContent: "center",
                  }}
                >
                  <p
                    style={{
                      margin: 0,
                      marginBottom: "2.5rem",
                      fontSize: "1.4rem",
                    }}
                  >
                    Bạn chưa có tài khoản ? &nbsp;
                  </p>
                  <NavLink className="login-rg" to="/register">
                    Đăng ký ngay.
                  </NavLink>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
