import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import axios from "axios";
import PopupModal from "./PopupModal";
import Loading from "./Loading";
import Nav from "./Nav";
import "../assets/css/home.css";
import { Select } from "antd";
import { GoLocation } from "react-icons/go";
function Home() {
  const dataCar = [
    {
      id: 1,
      img: "https://n1-pstg.mioto.vn/cho_thue_xe_o_to_tu_lai_thue_xe_du_lich_hochiminh/chevrolet_trailblazer_2018/p/g/2020/08/28/15/a1UTc6FqL0hXlm5ys3FBAA.jpg",
      name: "CHEVROLET TRAILBLAZER 2018",
      cost: 1200000,
      location: "Hồ Chí Minh",
      location_code: 1,
      type: "Số tự động",
    },
  ];

  return (
    <>
      {/* {isLoading ? <Loading /> : null} */}
      {/* <PopupModal
        isModalOpen={isModalOpen}
        setIsModalOpen={setIsModalOpen}
        doctorBooking={doctorBooking}
        dataBooking={dataBooking}
        setDataBooking={setDataBooking}
      /> */}
      <div className="home-container">
        <div className="home-content">
          <div className="home-bg">
            <Nav />
            <div className="home-items">
              <div className="home-slider">
                <div className="slider-header">
                  <p>CÙNG BẠN TRÊN MỌI HÀNH TRÌNH !</p>
                </div>
                <div className="slider-content">
                  <div>Chọn Tỉnh/Thành Phố:</div>
                  <Select
                    placeholder="Chọn tỉnh/thành phố..."
                    style={{
                      width: "65%",
                    }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="home-items-car-content">
          <p>Đặt thuê xe ngay ở đây !</p>
          <div className="home-items-car">
            {dataCar?.map((data) => {
              return (
                <div className="home-item-car" key={data.id}>
                  <div className="img-car">
                    <img src={data.img} alt="" />
                  </div>
                  <di className="text-card">
                    <div className="name-car">
                      <p>CHEVROLET TRAILBLAZER 2018</p>
                    </div>
                    <div className="type-car">
                      <p>Số tự động</p>
                    </div>
                    <div className="cost-car">
                      <p>Giá thuê: </p>
                      <p>1.200.000 VND</p>
                    </div>
                    <div className="location-car">
                      <i>
                        {" "}
                        <GoLocation />
                      </i>
                      <p>Hồ Chí Minh</p>
                    </div>
                  </di>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
