import React, { useEffect, useState } from "react";

// import { LoadingScreens, Loading } from "../LoadingSpin";
// import { useSelector } from "react-redux";
// import constants from "../../../redux/action/constants";

import { Modal, Select, Button, Steps, Card, Input } from "antd";
import { AiOutlineArrowLeft } from "react-icons/ai";
import { AiOutlineCheck } from "react-icons/ai";
import { AiOutlineClose } from "react-icons/ai";

const { Step } = Steps;

const PopupModal = ({
  isModalOpen,
  setIsModalOpen,
  dataBookingSocket,
  dataDoctorBooking,
}) => {
  const cashBooking = 560000;
  const time = [
    {
      key: 0,
      time: "08:00 - 10:00",
    },
    {
      key: 1,
      time: "10:00 - 12:00",
    },
    {
      key: 2,
      time: "14:00 - 16:00",
    },
    {
      key: 3,
      time: "16:00 - 18:00",
    },
    {
      key: 4,
      time: "19:00 - 21:00",
    },
  ];
  const [isLoading, setIsLoading] = useState(false);
  const [isVali, setIsVali] = useState(false);
  const [isTypePay, setIsTypePay] = useState();
  const [isStep, setIsStep] = useState(0);
  const [isPay, setIsPay] = useState(false);
  const [activeTime, setActiveTime] = useState();

  const [dataPayment, setDataPayment] = useState();
  // const dataPersonDoctor = useSelector(
  //   (state) => state.getProfileDoctorDetail.data
  // );
  const [dataBooking, setDataBooking] = useState({
    name: null,
    typeBooking: null,
    time: null,
    text: null,
    add: null,
    idBS: null,
  });
  const handleCancel = () => {
    setDataBooking({
      name: null,
      typeBooking: null,
      time: null,
      text: null,
      add: null,
    });
    setActiveTime();
    setIsModalOpen(false);
    setIsStep(0);
  };

  const handleOk = () => {
    // if (
    //   dataBooking.text !== null &&
    //   dataBooking.typeBooking !== null &&
    //   dataBooking.time !== null &&
    //   dataBooking.add !== null &&
    //   activeTime !== undefined
    // ) {
    //   setDataBooking((data) => {
    //     return {
    //       ...data,
    //       name: dataPersonDoctor?.fullName,
    //       idBS: dataPersonDoctor?.id,
    //     };
    //   });
    //   setIsStep(1);
    // } else {
    //   setIsVali(true);
    // }
  };
  const [windowDimenion, detectHW] = useState({
    winWidth: window.innerWidth - 250,
  });
  const detectSize = () => {
    detectHW({
      winWidth: window.innerWidth - 250,
    });
  };
  useEffect(() => {
    window.addEventListener("resize", detectSize);

    return () => {
      window.removeEventListener("resize", detectSize);
    };
  }, [windowDimenion]);

  // useEffect(() => {
  //   if (isVali) {
  //     const resetValid = setTimeout(() => {
  //       setIsVali(false);
  //     }, 2000);
  //     return () => {
  //       clearTimeout(resetValid);
  //     };
  //   }
  // }, [isVali]);

  const closePopup = () => {
    // setDataBooking({
    //   name: null,
    //   typeBooking: null,
    //   time: null,
    //   text: null,
    //   add: null,
    //   idBS: null,
    // });
    // setIsStep(0);
    // handleCancel();
    // dataBookingSocket(dataBooking);
  };

  const paymentBooking = () => {
    // if (
    //   dataBooking.name !== null &&
    //   dataBooking.text !== null &&
    //   dataBooking.typeBooking !== null &&
    //   dataBooking.time !== null &&
    //   dataBooking.add !== null &&
    //   dataBooking?.idBS !== null &&
    //   activeTime !== undefined
    // ) {
    //   setIsLoading(true);
    //   const dataPayMedical = {
    //     idUser: localStorage.getItem("id"),
    //     cashBooking: cashBooking,
    //     dataBooking: dataBooking,
    //   };
    //   try {
    //     fetch("http://localhost:8000/api/payment/patient/money/paybooking", {
    //       method: "POST",
    //       headers: {
    //         "Content-Type": "application/json",
    //         Token: `Bearer ${localStorage.getItem("token")}`,
    //       },
    //       body: JSON.stringify(dataPayMedical),
    //     })
    //       .then((res) => res.json())
    //       .then((data) => {
    //         if (data) {
    //           setIsLoading(false);
    //           setIsPay(false);
    //           setIsStep(2);
    //           setDataPayment(data);
    //           setIsStep(2);
    //         }
    //       });
    //   } catch (error) {
    //     console.log(error);
    //   }
    // }
  };

  return (
    <>
      {/* {isLoading ? <LoadingScreens /> : null} */}
      <Modal
        title="Thanh toán"
        open={isPay}
        onCancel={() => setIsPay(false)}
        zIndex={999}
        style={{
          top: "20%",
        }}
        footer={null}
      >
        <Card
          style={{
            width: "100%",
          }}
        >
          <div className="card-pay">
            <p>Dịch vụ: </p>
            <p>Đặt lịch khám bệnh</p>
          </div>
          <div className="card-pay">
            <p>Nguồn tiền: </p>
            <p> Ví Alo-pay</p>
          </div>
          <div className="card-pay">
            <p>Số tiền: </p>
            <p>
              {cashBooking.toLocaleString("it-IT", {
                style: "currency",
                currency: "VND",
              })}
            </p>
          </div>
        </Card>
        <div className="popup-booking-btn">
          <Button
            className="btn-pay-medical"
            htmlType="submit"
            onClick={paymentBooking}
          >
            Thanh toán
          </Button>
        </div>
      </Modal>
      <Modal
        title="Đặt lịch khám  "
        open={isModalOpen}
        onCancel={handleCancel}
        width={windowDimenion.winWidth}
        footer={null}
        zIndex={990}
      >
        <div className="popup-booking">
          <div style={{ display: "flex" }}>
            <div
              onClick={() => {
                if (isStep !== 0) {
                  setIsStep(isStep - 1);
                }
              }}
              style={
                isStep === 1
                  ? {
                      display: "flex",
                      justyfiContent: "center",
                      alignItems: "center",
                      color: "#0d6efd",
                      cursor: "pointer",
                    }
                  : { display: "none" }
              }
            >
              <AiOutlineArrowLeft />

              <span style={{ marginLeft: "10px" }}>Trở lại</span>
            </div>
            <Steps
              current={isStep}
              size="small"
              style={{
                marginBottom: "35px",
                width: "200px",
                marginLeft: "auto",
                marginRight: "auto",
              }}
            >
              <Step />
              <Step />
              <Step />
            </Steps>
          </div>
          {isStep === 0 ? (
            <div>
              <div className="popup-booking-container">
                <div className="popup-booking-detail-left">
                  <div className="popup-booking-detail-left-name">
                    <p>Bác sĩ: &nbsp;</p>
                    <p style={{ fontWeight: "700" }}>Quách Tuấn ANh</p>
                  </div>
                  <div className="popup-booking-detail-left-type">
                    <label>Chọn hình thức:</label>
                    <Select
                      value={dataBooking.typeBooking}
                      placeholder="Vui lòng chọn hình thức..."
                      style={{
                        width: "100%",
                      }}
                      options={[
                        {
                          value: 1,
                          label: "Tại nhà",
                        },
                        {
                          value: 2,
                          label: "Chăm sóc tại nhà",
                        },
                        {
                          value: 3,
                          label: "Trung tâm",
                        },
                      ]}
                    />
                  </div>
                  {dataBooking.typeBooking === 3 ? (
                    <div className="popup-booking-detail-left-type">
                      <label>Chọn trung tâm:</label>
                      <Select
                        value={dataBooking.add}
                        placeholder="Vui lòng chọn trung tâm..."
                        style={{
                          width: "100%",
                        }}
                        onChange={(value) => {
                          if (dataPersonDoctor) {
                            setDataBooking((data) => {
                              return { ...data, add: value };
                            });
                          }
                        }}
                        options={[
                          {
                            value: "Bệnh viện Bạch Mai",
                            label: "Bệnh viện Bạch Mai",
                          },
                          {
                            value: "Bệnh viện Hồng Ngọc",
                            label: "Bệnh viện Hồng Ngọc",
                          },
                          {
                            value: "Bệnh viện Đa Khoa",
                            label: "Bệnh viện Đa Khoa",
                          },
                        ]}
                      />
                    </div>
                  ) : (
                    <div className="popup-booking-detail-left-type">
                      <label>Nhập địa chỉ:</label>
                      <Input
                        value={dataBooking.add}
                        placeholder="Vui lòng nhập địa chỉ"
                        style={{
                          width: "100%",
                        }}
                        onChange={(e) => {
                          if (dataPersonDoctor) {
                            setDataBooking((data) => {
                              return { ...data, add: e.target.value };
                            });
                          }
                        }}
                      />
                    </div>
                  )}
                </div>
                <div className="popup-booking-detail-right">
                  <div className="popup-booking-detail-time">
                    <label>Chọn thời gian:</label>
                    <div className="popup-booking-detail-time-booking">
                      {time &&
                        time?.map((data) => {
                          return (
                            <span
                              onClick={() =>
                                setDataBooking((pre) => {
                                  return { ...pre, time: data.time };
                                }) & setActiveTime(data.key)
                              }
                              key={data.key}
                              className={
                                activeTime === data.key ? "active-time" : ""
                              }
                            >
                              {data.time}
                            </span>
                          );
                        })}
                    </div>
                  </div>
                </div>
              </div>

              <div
                className="popup-booking-detail-left-text"
                style={{ margin: "0 20px" }}
              >
                <label>Lời nhắn cho bác sĩ</label>
                <textarea
                  value={dataBooking.text === null ? "" : dataBooking.text}
                  onChange={(e) => {
                    setDataBooking((pre) => {
                      return { ...pre, text: e.target.value };
                    });
                  }}
                  className="form-control"
                  placeholder="Nhập nội dung..."
                ></textarea>
              </div>
              {isVali ? (
                <p
                  style={{
                    textAlign: "center",
                    color: "red",
                    marginTop: "15px",
                  }}
                >
                  Vui lòng nhập đầy đủ thông tin !
                </p>
              ) : (
                <p
                  style={{
                    textAlign: "center",
                    color: "red",
                    marginTop: "15px",
                  }}
                >
                  {" "}
                  &nbsp;{" "}
                </p>
              )}
              <div className="popup-booking-btn">
                <Button
                  className="btn-pay-medical"
                  htmlType="submit"
                  onClick={handleOk}
                >
                  Tiếp tục
                </Button>
              </div>
            </div>
          ) : isStep === 1 ? (
            <div>
              <Card
                style={{
                  width: "100%",
                }}
              >
                <div className="card-pay">
                  <p>Tên bác sĩ: </p>
                  <p>{dataBooking?.name}</p>
                </div>
                <div className="card-pay">
                  <p>Dịch vụ: </p>
                  <p>Đặt lịch khám bệnh</p>
                </div>
                <div className="card-pay">
                  <p>Nơi khám: </p>
                  <p>{dataBooking?.add}</p>
                </div>
                <div className="card-pay">
                  <p>Hình thức</p>
                  <p>
                    {dataBooking?.typeBooking === 1
                      ? "Tại nhà"
                      : dataBooking?.typeBooking === 2
                      ? "Chăm sóc tại nhà"
                      : "Trung tâm"}
                  </p>
                </div>
                <div className="card-pay">
                  <p>Thời gian khám: </p>
                  <p> {dataBooking?.time}</p>
                </div>
                <div className="card-pay">
                  <p>Số tiền: </p>
                  <p>
                    {Number(cashBooking).toLocaleString("it-IT", {
                      style: "currency",
                      currency: "VND",
                    })}
                  </p>
                </div>
              </Card>
              <Select
                value={isTypePay?.label}
                placeholder="Vui lòng chọn hình thức thanh toán..."
                style={{
                  width: "100%",
                  marginTop: "25px",
                }}
                onChange={(value, label) => {
                  setIsTypePay(label);
                }}
                options={[
                  {
                    value: 1,
                    label: "Ví Alo-Pay",
                  },
                  {
                    value: 2,
                    label: "Thanh toán tiền mặt",
                  },
                ]}
              />
              {isVali ? (
                <p
                  style={{
                    textAlign: "center",
                    color: "red",
                    marginTop: "15px",
                  }}
                >
                  Vui lòng nhập đầy đủ thông tin !
                </p>
              ) : (
                <p
                  style={{
                    textAlign: "center",
                    color: "red",
                    marginTop: "15px",
                  }}
                >
                  {" "}
                  &nbsp;{" "}
                </p>
              )}
              <div className="popup-booking-btn">
                <Button
                  className="btn-pay-medical"
                  htmlType="submit"
                  onClick={() => {
                    if (isTypePay) {
                      isTypePay.value === 1 ? setIsPay(true) : setIsStep(2);
                    } else {
                      setIsVali(true);
                    }
                  }}
                >
                  Tiếp tục
                </Button>
              </div>
            </div>
          ) : isStep === 2 ? (
            <div className="pay-result-container" style={{ margin: "0" }}>
              <div className="pay-result-main">
                <div className="pay-result-header">
                  {dataPayment?.resultPayment ? (
                    <i>
                      <AiOutlineCheck />
                    </i>
                  ) : !dataPayment?.resultPayment ? (
                    <i>
                      <AiOutlineClose />
                    </i>
                  ) : null}
                  {dataPayment?.resultPayment ? (
                    <h6>Đặt lịch thành công</h6>
                  ) : !dataPayment?.resultPayment ? (
                    <h6>Đặt lịch thất bại</h6>
                  ) : null}

                  <p>Cảm ơn bạn đã tin tưởng và sử dụng dịch vụ của AloPay !</p>
                </div>

                <div
                  className="pay-result-detail"
                  style={{ margin: "20px 40px", paddingBottom: "10px" }}
                >
                  <p>Chi tiết giao dịch</p>
                  <div
                    style={{ display: "flex", justifyContent: "space-between" }}
                  >
                    <p>Số tiền thanh toán:</p>
                    <p style={{ fontWeight: "600" }}>
                      {Number(
                        dataPayment?.data.dataPayment.cost
                      ).toLocaleString("it-IT", {
                        style: "currency",
                        currency: "VND",
                      })}
                    </p>
                  </div>
                  <div
                    style={{ display: "flex", justifyContent: "space-between" }}
                  >
                    <p>Mã giao dịch:</p>
                    <p style={{ fontWeight: "600" }}>
                      {dataPayment?.data.dataPayment.paymentCode}
                    </p>
                  </div>

                  <div
                    className="button-pay-resutl-main"
                    style={{ display: "flex", justifyContent: "center" }}
                  >
                    <button
                      className="button-pay-resutl back-to-history"
                      style={{ marginTop: "20px" }}
                    >
                      Lịch sử giao dịch
                    </button>
                    <button
                      style={{ marginTop: "20px" }}
                      className="button-pay-resutl back-to-home"
                      onClick={closePopup}
                    >
                      Đóng
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ) : null}
        </div>
      </Modal>
    </>
  );
};

export default PopupModal;
